package com;

public class Condition3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int a=1000,b=1000,c=1000;
     
 
     
   if(a>b && a>c)
   { 
	   if(b>a && b>c)
	   {	   
	        System.out.println("a is max");
	   }
      
       else 
       {  
	         System.out.println("b is max");
	   
	   }
	}
	else
	  {
		System.out.println("c is max");
		
	  }
	}

}
