package com;

public class Swappingg {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int c=20,d=15;
    		// before swapping
    		
    	c=c+d;
    	d=c-d;
    	c=c-d;
    	// after swapping
    System.out.println("after swapping: c =" + c +  ", d  = " + d );
           
    		
    
	}

}
